  buildArr(theArr) {
    let step = 10;
    var arrOfarr = [];
    for(var i = 0; i < theArr.length ; i+=step) {
        var row = [];
        for(var x = 0; x < step; x++) {
          var value = theArr[i + x];
            if (!value) {
                break;
            }
            row.push(value);
        }
        arrOfarr.push(row);
    }
    return arrOfarr;
  }

